class rich_rank_day_Fragment : Fragment() {
    override fun onCreateView(
        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        return inflater.inflate(R.layout.rich_day_rank, container, false)
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        val dataList = init()

        val first = dataList[0]
        Glide.with(view.context).load(first.userInfo.headUrl).apply(RequestOptions().circleCrop()).override(65,65).into(iv_rank01)
        tv_name_01.text = first.userInfo.nick
        tv_rich_01.text = first.value.toString()
        dataList.removeAt(0)

        val second = dataList[0]
        Glide.with(view.context).load(second.userInfo.headUrl).apply(RequestOptions().circleCrop()).into(iv_rank02)
        tv_name_02.text = second.userInfo.nick
        tv_rich_02.text = second.value.toString()
        dataList.removeAt(0)

        val third = dataList[0]
        Glide.with(view.context).load(third.userInfo.headUrl).apply(RequestOptions().circleCrop()).into(iv_rank03)
        tv_name_03.text = third.userInfo.nick
        tv_rich_03.text = third.value.toString()
        dataList.removeAt(0)

        val item = dataList.last()
        tv_rank_no_bg.text = item.rank
        tv_day_username.text = item.userInfo.nick
        Glide.with(view.context).load(item.userInfo.headUrl).apply(RequestOptions().circleCrop()).into(iv_avatar)
        Glide.with(view.context).load(item.userInfo.wealthLevel.image_url).centerCrop().into(iv_icon1)
        Glide.with(view.context).load(item.userInfo.charmLevel.image_url).centerCrop().into(iv_icon2)
        if (item.userInfo.is_liang_num) {
            iv_icon3.setImageResource(R.drawable.lh)
        }
        tv_rich_num_day.text = item.value.toString()
        if(item.userInfo.sex==0){
            iv_gender.setImageResource(R.drawable.female)
        }else if(item.userInfo.sex==1){
            iv_gender.setImageResource(R.drawable.male)
        }

        val layoutManager = LinearLayoutManager(context)
        layoutManager.orientation = LinearLayoutManager.VERTICAL
        rl_rank.layoutManager = layoutManager

        val adapter = RankDayAdapter(view.context, dataList)
        rl_rank.adapter = adapter

    }

    class RankDayAdapter(val context: Context, val userList: ArrayList<User>) :
        RecyclerView.Adapter<RankDayAdapter.ViewHolder>() {

        class ViewHolder(view: View) : RecyclerView.ViewHolder(view) {
            val userName: TextView = view.findViewById(R.id.tv_username)
            val richNum: TextView = view.findViewById(R.id.tv_rich_day_num)
            val avatar: ImageView = view.findViewById(R.id.iv_avatar)
            val rank: TextView = view.findViewById(R.id.tv_rank_no_bg)

            //名字下的小图标
            val gender: ImageView = view.findViewById(R.id.iv_gender)
            val icon1: ImageView = view.findViewById(R.id.iv_icon1)
            val icon2: ImageView = view.findViewById(R.id.iv_icon2)
            val icon3: ImageView = view.findViewById(R.id.iv_icon3)

        }

        override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
//            TODO("Not yet implemented")
            val view = LayoutInflater.from(parent.context)
                .inflate(R.layout.recyclerview_rank_item, parent, false)
            return ViewHolder(view)
        }

        override fun onBindViewHolder(holder: ViewHolder, position: Int) {
//            TODO("Not yet implemented")
            val item: User = userList[position]
//            item.get(0).userInfo.age
            val integer = Integer.parseInt(holder.rank.text.toString())
            Glide.with(context).load(item.userInfo.headUrl).apply(RequestOptions().circleCrop())
                .into(holder.avatar)
            holder.userName.text = item.userInfo.nick
            holder.richNum.text = item.value.toString()
            holder.rank.text = item.rank
            Glide.with(context).load(item.userInfo.wealthLevel.image_url).centerCrop()
                .into(holder.icon1)
            Glide.with(context).load(item.userInfo.charmLevel.image_url).centerCrop()
                .into(holder.icon2)
            if (item.userInfo.is_liang_num) {
                holder.icon3.setImageResource(R.drawable.lh)
            }
            if(item.userInfo.sex == 0){
                holder.gender.setImageResource(R.drawable.female)
            }else if(item.userInfo.sex == 1){
                holder.gender.setImageResource(R.drawable.male)
            }
        }

        override fun getItemCount() = userList.size

    }

